import React, { useState } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const FormQuote = () => {
  const [dataQuote, setdataQuote] = useState({
    nom: '',
    telephone: '',
    email: '',
    matiere: '',
    poids: '',
    adresse_depart: '',
    adresse_arrivee: ''
  });

  const changeInput = (event) =>
    setdataQuote({ ...dataQuote, [event.target.name]: event.target.value });

  const submitInput = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:5000/quote', dataQuote);
      if (response.status === 200) {
        toast.success('Devis soumis avec succès');
        setdataQuote({
          nom: '',
          telephone: '',
          email: '',
          matiere: '',
          poids: '',
          adresse_depart: '',
          adresse_arrivee: ''
        });
      } else {
        toast.error('Erreur lors de la soumission');
      }
    } catch (error) {
      toast.error('Erreur lors de la soumission');
    }
  };

  return (
    <div className="form-quote-container">
      <ToastContainer 
        position="top-center" 
        autoClose={5000} 
        hideProgressBar={false} 
        closeOnClick 
        pauseOnHover 
        draggable 
      />
      <form onSubmit={submitInput} className="form-quote p-4 shadow rounded">
        <h1 className="text-center text-decoration-underline mb-4 fs-2">DEMANDE DE DEVIS</h1>
        <div className="mb-3">
          <label className="form-label">Nom/Prénom ou Entreprise:</label>
          <input
            type="text"
            name="nom"
            value={dataQuote.nom}
            onChange={changeInput}
            className="form-control"
            required
          />
        </div>
        {/* Reste du formulaire */}
      </form>
    </div>
  );
};

export default FormQuote;
