import React, { useState } from 'react';
import axios from 'axios';
import './styles.css';

const FormQuote = () => {
  const [dataQuote, setdataQuote] = useState({
    nom: '',
    telephone: '',
    email: '',
    matiere: '',
    poids: '',
    adresse_depart: '',
    adresse_arrivee: ''
  });

  const changeInput = (event) => setdataQuote({ ...dataQuote, [event.target.name]: event.target.value });

  const submitInput = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:5000/quote', dataQuote);
      if (response.status === 200) {
        alert('Devis soumis avec succès');
        setdataQuote({
          nom: '',
          telephone: '',
          email: '',
          matiere: '',
          poids: '',
          adresse_depart: '',
          adresse_arrivee: ''
        });
      } else {
        alert('Erreur lors de la soumission');
      }
    } catch (error) {
      alert('Erreur lors de la soumission');
    }
  };

  return (
    <form onSubmit={submitInput} className="container mt-5 p-4 rounded shadow bg-light">
      <h1 className="text-center mb-4">Demande de Devis</h1>

      <div className="form-group mb-3">
        <label>Nom - Prenom / Entreprise:</label>
        <input type="text" name="nom" value={dataQuote.nom} onChange={changeInput} className="form-control" placeholder="Votre nom ou entreprise" />
      </div>

      <div className="form-group mb-3">
        <label>Téléphone:</label>
        <input type="text" name="telephone" value={dataQuote.telephone} onChange={changeInput} className="form-control" placeholder="Votre numéro de téléphone" />
      </div>

      <div className="form-group mb-3">
        <label>Email:</label>
        <input type="email" name="email" value={dataQuote.email} onChange={changeInput} className="form-control" placeholder="Votre adresse email" />
      </div>

      <div className="form-group mb-3">
        <label>Matériel:</label>
        <input type="text" name="matiere" value={dataQuote.matiere} onChange={changeInput} className="form-control" placeholder="Matériel à transporter" />
      </div>

      <div className="form-group mb-3">
        <label>Poids à transporter:</label>
        <input type="text" name="poids" value={dataQuote.poids} onChange={changeInput} className="form-control" placeholder="Poids à transporter" />
      </div>

      <div className="form-group mb-3">
        <label>Adresse de départ:</label>
        <input type="text" name="adresse_depart" value={dataQuote.adresse_depart} onChange={changeInput} className="form-control" placeholder="Adresse de départ" />
      </div>

      <div className="form-group mb-3">
        <label>Adresse d'arrivée:</label>
        <input type="text" name="adresse_arrivee" value={dataQuote.adresse_arrivee} onChange={changeInput} className="form-control" placeholder="Adresse d'arrivée" />
      </div>

      <button type="submit" className="btn btn-primary w-100">Envoyer</button>
    </form>
  );
};

export default FormQuote;
